INSERT INTO `goods` VALUES ('10', 'http://7vzmoo.com2.z0.glb.qiniucdn.com/Paninkiller_01.jpg,http://7vzmoo.com2.z0.glb.qiniucdn.com/Paninkiller_02.jpg', '斩妖除魔 合集', 'Painkiller Complete Pack', '160.0', '射击', '<div class=\"content\"><p><strong>此合集包含以下内容：</strong></p>\r\n<p>斩妖除魔：沉溺</p>\r\n<p>斩妖除魔：救赎</p>\r\n<p>斩妖除魔：黑盒版</p>\r\n<p>斩妖除魔：邪恶再生</p>\r\n<p>斩妖除魔：地狱诅咒</p>\r\n<p>斩妖除魔：地狱诅咒 - 城市魔物</p>\r\n<p>斩妖除魔：地狱诅咒 - 血海的恶魔假期</p>\r\n<p>斩妖除魔：地狱诅咒 - 全金属火箭</p>\r\n<p>斩妖除魔：地狱诅咒 - 中世纪恐怖</p>\r\n<p>斩妖除魔：地狱诅咒 - “僵尸堡垒”行动</p>\r\n<p>斩妖除魔：地狱诅咒 - 圣诞老人DLC</p>\r\n<p>斩妖除魔：地狱诅咒 - 午夜杀人钟</p></div>', '<div><div class=\"product-term-container\"><div class=\"term-title\">开发商</div><div class=\"item-container\"><div class=\"term-items\">The Farm 51</div><div class=\"term-items\">People Can Fly</div><div class=\"term-items\">Mindware Studios</div><div class=\"term-items\">Eggtooth Team</div><div class=\"term-items\">Med-Art</div><div class=\"term-items\">Homegrown Games</div></div></div><div class=\"product-term-container\"><div class=\"term-title\">发行商</div><div class=\"item-container\"><div class=\"term-items\">THQ Nordic</div></div></div><div class=\"product-term-container\"><div class=\"term-title\">发行日期</div><div class=\"item-container\"><div class=\"term-items date\">2015-07-10</div></div></div><div class=\"product-term-container\"><div class=\"term-title\">游戏语言</div><div class=\"item-container\"><div class=\"term-items\">英文</div></div></div><div class=\"product-term-container\"><div class=\"term-title\">下载方式</div><div class=\"item-container\"><div class=\"term-items\">第三方平台</div></div></div><div class=\"product-term-container\"><div class=\"term-title\">支持系统</div><div class=\"item-container\"><div class=\"term-items\">Win</div></div></div></div>', '<table class=\"system-parameter-table\"><tbody><tr><td class=\"lab\">操作系统</td><td class=\"info\">64-bit Windows 10 or later</td></tr><tr><td class=\"lab\">内存</td><td class=\"info\">16 GB RAM</td></tr><tr><td class=\"lab\">DirectX 版本</td><td class=\"info\">